!function ($) {
  "use strict";

  var bEvent = function () {

    // A jQuery plugin for fluid width video embeds
    // function responsiveVideo() {
    //   if ($.fn.fitVids) {
    //     $(".video").fitVids()
    //   }
    // }
    // A custom select for Bootstrap using button dropdown
    function selectpicker() {
      if ($.fn.selectpicker) {
        $(".selectpicker").selectpicker();
      }
    }
    // A jQuery plugin for the navigation on one-page sites
    function navigation() {
      if ($.fn.onePageNav && $("#nav").length) {
        if ($('#nav').attr('we-nav-scroll')) {
          $('#nav').onePageNav({
            currentClass: 'active',
            scrollSpeed: 600,
            scrollOffset: 90,
            scrollThreshold: 0.2,
            changeHash: true,
            easing: 'swing',
            filter: ':not(.external)'
          });
        }

      }
    }
    // Date countdown plugin for jQuery
    function countdown() {
      var count = $('#countdown');
      if ($.fn.countdown && count.length) {
        count.countdown(count.data("date"), function (event) {
          var $this = $(this);
          switch (event.type) {
          case "seconds":
          case "minutes":
          case "hours":
          case "days":
          case "weeks":
          case "daysLeft":
            $this.find('p#' + event.type).html(event.value);
            break;
          case "finished":
            $this.hide();
            break;
          }
        });
      }
    }
    // Preloader
    function preloader() {
      $(window).load(function () {
        $(".preloader").fadeOut();
        $("body").removeClass("remove-scroll");
      });
    }
    // A jQuery plugin that enables HTML5 placeholder behavior for browsers that arenâ€™t trying hard enough yet
    function placeholderIE() {
      if ($.fn.placeholder) {
        $("input, textarea").placeholder();
      }
    }
    // Reveal Animations When You Scroll
    function wow() {
      $(window).load(function () {
         new WOW().init();
      });
    }
    // Custom scripts
    // function app() {
    //   $("#back").on("click", function () {
    //     $(".register").removeClass("success")
    //   })
    // }
    // Return all functions
    return {
      init: function () {
        preloader();
        // responsiveVideo()
        selectpicker();
        navigation();
        countdown();
        placeholderIE();
        wow();
        // app()
      }
    };
  }();

  $(function () {
    // Launch functions
    bEvent.init();
  });
}(window.jQuery);